function get_token(n,t){var o="http://challenge.code2040.org/api/register",i={email:n,github:t};i=JSON.stringify(i);var a;return $.ajax({type:"POST",url:o,async:!1,data:i,dataType:"json"}).done(function(n){a=n.result}),a}function change_login_box(n,t,o){$("#login-box").html('<div id="login-box" class="col-sm-8 text-right"><strong>Email: </strong>'+n+"<br />Github: "+t+"<br />Token: "+o+" <br /></div>")}$(function(){$("#login-btn").click(function(){var n=$("#email-form").val(),t=$("#github-form").val(),o=get_token(n,t);change_login_box(n,t,o)})});